-- Table structure for college
-- ----------------------------
CREATE TABLE `college` (
  `col_id` bigint(20) NOT NULL auto_increment,
  `col_title` varchar(100) collate utf8_bin NOT NULL,
  `col_author` varchar(30) collate utf8_bin default NULL,
  `col_source` varchar(30) collate utf8_bin default NULL COMMENT '各院系名称',
  `col_content` text collate utf8_bin,
  `col_type` varchar(20) collate utf8_bin NOT NULL COMMENT '所属院系',
  `col_flag` varchar(10) collate utf8_bin NOT NULL,
  `col_date` varchar(20) collate utf8_bin default NULL,
  `col_image` varchar(10) collate utf8_bin default NULL,
  PRIMARY KEY  (`col_id`)
