-- Table structure for message
-- ----------------------------
CREATE TABLE `message` (
  `meg_id` bigint(20) NOT NULL auto_increment COMMENT '主键ID',
  `meg_name` varchar(20) NOT NULL COMMENT '留言者昵称',
  `meg_title` varchar(50) default NULL COMMENT '留言主题',
  `meg_content` varchar(600) NOT NULL COMMENT '留言内容',
  `meg_time` varchar(15) NOT NULL COMMENT '留言时间',
  `reply_time` varchar(15) default NULL,
  `reply` varchar(500) default NULL,
  `flag` char(255) NOT NULL default '0' COMMENT '标记，是否审核,0是审核,1是已审核',
  PRIMARY KEY  (`meg_id`)
) ENGINE=InnoDB AUTO_INCREMENT=18 DEFAULT CHARSET=utf8;

