  `news_flag` varchar(10) NOT NULL COMMENT '章文标记',
  `news_date` varchar(20) default NULL COMMENT '文章上传时间',
  `news_image` varchar(10) default NULL,
  PRIMARY KEY  (`news_id`)
) ENGINE=InnoDB AUTO_INCREMENT=137 DEFAULT CHARSET=utf8;

